import React, { useState, useEffect } from 'react';
import './Calendar.css';

const Calendar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [currentDate, setCurrentDate] = useState(new Date());
  const [selectedDate, setSelectedDate] = useState(new Date());

  // Mock data for upcoming events/contests
  const upcomingEvents = [
    {
      id: 1,
      title: 'Monthly Certification Exam',
      date: new Date(2025, 0, 15), // January 15, 2025
      type: 'exam',
      duration: '2 hours'
    },
    {
      id: 2,
      title: 'Web Development Contest',
      date: new Date(2025, 0, 20), // January 20, 2025
      type: 'contest',
      duration: '3 hours'
    },
    {
      id: 3,
      title: 'AI/ML Workshop',
      date: new Date(2025, 0, 25), // January 25, 2025
      type: 'workshop',
      duration: '4 hours'
    }
  ];

  // Mock streak data - in real app this would come from API
  const streakData = {
    currentStreak: 7,
    longestStreak: 15,
    totalActiveDays: 45,
    weeklyProgress: [
      { day: 'Mon', completed: true, intensity: 3 },
      { day: 'Tue', completed: true, intensity: 2 },
      { day: 'Wed', completed: true, intensity: 4 },
      { day: 'Thu', completed: true, intensity: 1 },
      { day: 'Fri', completed: false, intensity: 0 },
      { day: 'Sat', completed: true, intensity: 2 },
      { day: 'Sun', completed: true, intensity: 3 }
    ]
  };

  // Get current date information
  const today = new Date();
  const currentMonth = today.toLocaleDateString('en-US', { month: 'long', year: 'numeric' });
  const currentDay = today.toLocaleDateString('en-US', { weekday: 'long', day: 'numeric' });

  const formatDate = (date) => {
    return date.toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric'
    });
  };

  const formatTime = (date) => {
    return date.toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getEventTypeColor = (type) => {
    switch (type) {
      case 'exam': return '#ef4444';
      case 'contest': return '#3b82f6';
      case 'workshop': return '#10b981';
      default: return '#6b7280';
    }
  };

  const getUpcomingEvents = () => {
    return upcomingEvents.filter(event => event.date >= today);
  };

  const getNextEvent = () => {
    const upcoming = getUpcomingEvents();
    return upcoming.length > 0 ? upcoming[0] : null;
  };

  const nextEvent = getNextEvent();

  // Generate mini calendar for current month
  const generateMiniCalendar = () => {
    const year = today.getFullYear();
    const month = today.getMonth();
    const firstDay = new Date(year, month, 1);
    const lastDay = new Date(year, month + 1, 0);
    const daysInMonth = lastDay.getDate();
    const startingDayOfWeek = firstDay.getDay();

    const calendarDays = [];

    // Add empty cells for days before the first day of the month
    for (let i = 0; i < startingDayOfWeek; i++) {
      calendarDays.push(null);
    }

    // Add days of the month
    for (let day = 1; day <= daysInMonth; day++) {
      const date = new Date(year, month, day);
      const isToday = date.toDateString() === today.toDateString();
      const hasEvent = upcomingEvents.some(event => event.date.toDateString() === date.toDateString());

      // Mock activity data - in real app this would come from backend
      const dayOfWeek = date.getDay();
      const weekDayNames = ['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'];
      const activity = streakData.weeklyProgress.find(w => w.day === weekDayNames[dayOfWeek]);
      const intensity = activity ? activity.intensity : 0;

      calendarDays.push({
        day,
        date,
        isToday,
        hasEvent,
        intensity,
        isActive: intensity > 0
      });
    }

    return calendarDays;
  };

  return (
    <div className="calendar-container">
      <button className="calendar-toggle" onClick={toggleCalendar}>
        <svg width="22" height="22" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
          <rect x="3" y="4" width="18" height="18" rx="2" ry="2"/>
          <line x1="16" y1="2" x2="16" y2="6"/>
          <line x1="8" y1="2" x2="8" y2="6"/>
          <line x1="3" y1="10" x2="21" y2="10"/>
        </svg>
        <span className="calendar-badge">{getUpcomingEvents().length}</span>
      </button>

      {isOpen && (
        <div className="calendar-dropdown">
          <div className="calendar-header">
            <div className="current-date-info">
              <h3>{currentMonth}</h3>
              <p className="current-day">{currentDay}</p>
            </div>
            <button className="close-calendar" onClick={() => setIsOpen(false)}>
              √ó
            </button>
          </div>

          {/* Streak Information */}
          <div className="streak-section">
            <div className="streak-stats">
              <div className="streak-item">
                <div className="streak-number">{streakData.currentStreak}</div>
                <div className="streak-label">Current Streak</div>
              </div>
              <div className="streak-item">
                <div className="streak-number">{streakData.longestStreak}</div>
                <div className="streak-label">Best Streak</div>
              </div>
              <div className="streak-item">
                <div className="streak-number">{streakData.totalActiveDays}</div>
                <div className="streak-label">Total Active</div>
              </div>
            </div>

            {/* Next Upcoming Event Preview */}
            {nextEvent && (
              <div className="next-event-preview">
                <div className="next-event-header">
                  <span className="next-event-icon">üìÖ</span>
                  <span className="next-event-label">Next Event</span>
                </div>
                <div className="next-event-details">
                  <h4 className="next-event-title">{nextEvent.title}</h4>
                  <p className="next-event-time">
                    {formatDate(nextEvent.date)} ‚Ä¢ {formatTime(nextEvent.date)}
                  </p>
                </div>
              </div>
            )}
          </div>

          {/* Mini Calendar View */}
          <div className="mini-calendar">
            <div className="calendar-grid">
              {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
                <div key={day} className="calendar-day-header">{day}</div>
              ))}
              {generateMiniCalendar().map((dayInfo, index) => (
                <div
                  key={index}
                  className={`calendar-day ${dayInfo?.isToday ? 'today' : ''} ${dayInfo?.hasEvent ? 'has-event' : ''} ${dayInfo?.isActive ? 'active' : ''}`}
                  style={{ backgroundColor: dayInfo?.intensity ? getIntensityColor(dayInfo.intensity) : undefined }}
                >
                  {dayInfo ? (
                    <>
                      <span className="day-number">{dayInfo.day}</span>
                      {dayInfo.hasEvent && <span className="event-dot"></span>}
                    </>
                  ) : (
                    ''
                  )}
                </div>
              ))}
            </div>
          </div>

          <div className="events-list">
            {getUpcomingEvents().length === 0 ? (
              <div className="no-events">
                <p>No upcoming events</p>
              </div>
            ) : (
              <>
                {getUpcomingEvents().map(event => (
                  <div key={event.id} className="event-item">
                    <div className="event-header">
                      <div
                        className="event-type-indicator"
                        style={{ backgroundColor: getEventTypeColor(event.type) }}
                      ></div>
                      <h4 className="event-title">{event.title}</h4>
                    </div>
                    <div className="event-details">
                      <p className="event-date">
                        üìÖ {formatDate(event.date)} at {formatTime(event.date)}
                      </p>
                      <p className="event-duration">‚è±Ô∏è {event.duration}</p>
                    </div>
                    <button className="event-action-btn">
                      Join Event
                    </button>
                  </div>
                ))}
                {getUpcomingEvents().length > 3 && (
                  <div className="more-events-indicator">
                    +{getUpcomingEvents().length - 3} more events
                  </div>
                )}
              </>
            )}
          </div>

          <div className="calendar-footer">
            <button className="view-all-btn">
              View All Events
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Calendar;
