import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import apiRequest from '../utils/api';
import useAuth from '../hooks/useAuth';
import './CourseContent.css';

const CourseContent = () => {
  const { id } = useParams();
  const { user } = useAuth();
  const [modules, setModules] = useState([]);
  const [completed, setCompleted] = useState([]);
  const [course, setCourse] = useState(null);

  useEffect(() => {
    fetchContent();
  }, [id]);

  const fetchContent = async () => {
    try {
      const data = await apiRequest(`/courses/${id}/content`, 'GET', null, true);
      setModules(data.modules || []);
      setCompleted(data.completedModules || []);
      setCourse(data.course || null);
    } catch (err) {
      console.error('Failed to load course content', err);
    }
  };


  const handleCheck = async (moduleId, checked) => {
    try {
      if (checked) {
        await apiRequest(`/courses/${id}/modules/${moduleId}/complete`, 'POST', null, true);
        setCompleted((prev) => prev.includes(moduleId) ? prev : [...prev, moduleId]);
      } else {
        // Remove completion (new endpoint needed)
        await apiRequest(`/courses/${id}/modules/${moduleId}/incomplete`, 'POST', null, true);
        setCompleted((prev) => prev.filter(mid => mid !== moduleId));
      }
    } catch (err) {
      console.error('Failed to update progress', err);
    }
  };

  const progress = modules.length ? (completed.length / modules.length) * 100 : 0;

  return (
    <div className="course-content-page">
      <h2>{course ? course.title : 'Course Content'}</h2>
      <div className="progress-bar">
        <div className="progress" style={{ width: `${progress}%` }} />
      </div>
      <ul className="modules-list">
        {modules.map((mod) => (
          <li key={mod._id} className="module-item">
            <label>
              <input
                type="checkbox"
                checked={completed.includes(mod._id)}
                onChange={e => handleCheck(mod._id, e.target.checked)}
              />
              {mod.title}
            </label>
            <ul style={{ marginTop: 8, marginBottom: 0 }}>
              {mod.lessons && mod.lessons.length > 0 ? mod.lessons.map(lesson => (
                <li key={lesson._id} style={{ marginBottom: 4 }}>
                  <span style={{ fontWeight: 500 }}>{lesson.title}</span> ({lesson.contentType})
                  {lesson.contentType === 'text' && <span style={{ marginLeft: 8, color: '#555' }}>{lesson.content}</span>}
                  {lesson.contentType !== 'text' && lesson.contentUrl && (
                    <a href={`http://localhost:5000${lesson.contentUrl}`} target="_blank" rel="noopener noreferrer" style={{ marginLeft: 8 }}>View</a>
                  )}
                </li>
              )) : <li style={{ color: '#888' }}>No lessons yet.</li>}
            </ul>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CourseContent;
