import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import apiRequest from '../utils/api';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [otp, setOtp] = useState('');
  const [showOTP, setShowOTP] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const data = await apiRequest('/auth/login', 'POST', { email, password });

      if (data.requiresOTP) {
        // Show OTP input
        setShowOTP(true);
        alert('OTP has been sent to your email. Please check your inbox.');
      } else {
        // Direct login (shouldn't happen with new flow, but handle it)
        localStorage.setItem('token', data.token);
        window.location.href = '/';
      }
    } catch (err) {
      setError(err.message || 'Login failed. Please check your credentials.');
      alert(err.message || 'Login failed. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };

  const handleOTPVerify = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const data = await apiRequest('/auth/verify-otp', 'POST', { email, otp });
      localStorage.setItem('token', data.token);
      alert('Login successful!');
      window.location.href = '/';
    } catch (err) {
      setError(err.message || 'Invalid OTP. Please try again.');
      alert(err.message || 'Invalid OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleBackToLogin = () => {
    setShowOTP(false);
    setOtp('');
    setError('');
  };

  return (
    <div className="login-page">
      <div className="login-left">
        {!showOTP ? (
          <>
            <h1>Hello!</h1>
            <p>Sign in to your account</p>
            <form onSubmit={handleSubmit}>
              <div className="input-group">
                <span className="input-icon">üìß</span>
                <input
                  type="email"
                  placeholder="E-mail"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  disabled={loading}
                />
              </div>
              <div className="input-group">
                <span className="input-icon">üîí</span>
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  disabled={loading}
                />
              </div>
              <div className="form-footer">
                <label>
                  <input type="checkbox" /> Remember me
                </label>
                <Link to="/forgot" className="forgot-link">Forgot password?</Link>
              </div>
              {error && <p style={{ color: 'red', fontSize: '14px', marginBottom: '10px' }}>{error}</p>}
              <button type="submit" className="btn-login" disabled={loading}>
                {loading ? 'Sending OTP...' : 'SIGN IN'}
              </button>
            </form>
            <p className="signup-text">
              Don't have an account? <Link to="/register">Create</Link>
            </p>
          </>
        ) : (
          <>
            <h1>Verify OTP</h1>
            <p>Enter the 6-digit code sent to your email</p>
            <p style={{ fontSize: '14px', color: '#666', marginBottom: '20px' }}>
              Email: <strong>{email}</strong>
            </p>
            <form onSubmit={handleOTPVerify}>
              <div className="input-group">
                <span className="input-icon">üîê</span>
                <input
                  type="text"
                  placeholder="Enter 6-digit OTP"
                  value={otp}
                  onChange={(e) => setOtp(e.target.value.replace(/\D/g, '').slice(0, 6))}
                  required
                  maxLength={6}
                  disabled={loading}
                  style={{ textAlign: 'center', letterSpacing: '8px', fontSize: '20px', fontWeight: 'bold' }}
                />
              </div>
              {error && <p style={{ color: 'red', fontSize: '14px', marginBottom: '10px' }}>{error}</p>}
              <button type="submit" className="btn-login" disabled={loading || otp.length !== 6}>
                {loading ? 'Verifying...' : 'VERIFY OTP'}
              </button>
              <button 
                type="button" 
                onClick={handleBackToLogin}
                style={{ 
                  marginTop: '10px', 
                  background: 'transparent', 
                  border: 'none', 
                  color: '#4a90e2', 
                  cursor: 'pointer',
                  textDecoration: 'underline'
                }}
                disabled={loading}
              >
                Back to Login
              </button>
            </form>
          </>
        )}
      </div>
      <div className="login-right">
         <img src="https://dce.harvard.edu/wp-content/uploads/sites/7/2023/12/choosing-classes.jpg" alt="Login illustration" />
      </div>
    </div>
  );
};

export default Login;
