import React from 'react';
import { Link } from 'react-router-dom';
import useAuth from '../hooks/useAuth';
import logo from '../assets/logo.png';
import Calendar from './Calendar';
import './Header.css';

const Header = () => {
  const { user, logout } = useAuth();

  const renderDashboardLink = () => {
    if (!user) return null;
    if (user.role === 'student') {
      return <Link to="/dashboard/student">Student</Link>;
    }
    if (user.role === 'instructor') {
      return <Link to="/dashboard/instructor">Instructor</Link>;
    }
    if (user.role === 'admin') {
      return <Link to="/dashboard/admin">Admin</Link>;
    }
    return null;
  };

  return (
    <header className="site-header">
      <div className="header-inner">
        <Link to="/" className="brand">
          <img src={logo} alt="CertifyHub logo" />
          <div className="brand-meta">
            <span className="brand-title">CertifyHub</span>
            <span className="brand-tagline">Learning that matters</span>
          </div>
        </Link>

        <nav className="primary-nav">
          <Link to="/courses">Catalog</Link>
          <Link to="/about">About</Link>
          <Link to="/certificates">Certificates</Link>
          {renderDashboardLink()}
        </nav>

        <div className="header-utilities">
          <div className="calendar-trigger">
            <Calendar />
          </div>

          {user ? (
            <div className="user-controls">
              <Link to="/profile" className="user-chip">
                <span className="chip-label">Signed in</span>
                <strong>{user.name || user.email}</strong>
              </Link>
              <button className="btn btn-secondary" onClick={logout}>
                Logout
              </button>
            </div>
          ) : (
            <>
              <Link to="/login" className="btn btn-ghost">
                Sign in
              </Link>
              <Link to="/register" className="btn btn-primary">
                Start learning
              </Link>
            </>
          )}
        </div>
      </div>
    </header>
  );
};

export default Header;
