import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import apiRequest from '../utils/api';
import './Login.css';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [otp, setOtp] = useState('');
  const [showOTP, setShowOTP] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const data = await apiRequest('/auth/login', 'POST', { email, password });

      if (data.requiresOTP) {
        // Show OTP input
        setShowOTP(true);
        alert('OTP has been sent to your email. Please check your inbox.');
      } else {
        // Direct login (shouldn't happen with new flow, but handle it)
        localStorage.setItem('token', data.token);
        window.location.href = '/';
      }
    } catch (err) {
      setError(err.message || 'Login failed. Please check your credentials.');
      alert(err.message || 'Login failed. Please check your credentials.');
    } finally {
      setLoading(false);
    }
  };

  const handleOTPVerify = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const data = await apiRequest('/auth/verify-otp', 'POST', { email, otp });
      localStorage.setItem('token', data.token);
      alert('Login successful!');
      window.location.href = '/';
    } catch (err) {
      setError(err.message || 'Invalid OTP. Please try again.');
      alert(err.message || 'Invalid OTP. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleBackToLogin = () => {
    setShowOTP(false);
    setOtp('');
    setError('');
  };

  return (
    <div className="login-page">
      <div className="login-left">
        {!showOTP ? (
          <>
            <h1>Hello!</h1>
            <p>Sign in to your account</p>
            <form onSubmit={handleSubmit}>
              <div className="input-group">
                <span className="input-icon">üìß</span>
                <input
                  type="email"
                  placeholder="E-mail"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                  disabled={loading}
                />
              </div>
              <div className="input-group">
                <span className="input-icon">üîí</span>
                <input
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  disabled={loading}
                />
              </div>
              <div className="form-footer">
                <label>
                  <input type="checkbox" /> Remember me
                </label>
                <Link to="/forgot" className="forgot-link">Forgot password?</Link>
              </div>
              {error && <div className="error-message">{error}</div>}
              {success && <div className="success-message">{success}</div>}
              <button type="submit" className="btn-login" disabled={loading}>
                {loading ? (
                  <span className="btn-loading">
                    <span className="spinner"></span>
                    Sending OTP...
                  </span>
                ) : (
                  'SIGN IN'
                )}
              </button>
            </form>
            <p className="signup-text">
              Don't have an account? <Link to="/register">Create</Link>
            </p>
          </>
        ) : (
          <>
            <div className="otp-header">
              <div className="otp-icon">üîê</div>
              <h1>Verify OTP</h1>
              <p>Enter the 6-digit code sent to your email</p>
              <div className="email-display">
                <span className="email-label">Email:</span>
                <span className="email-value">{email}</span>
              </div>
            </div>
            <form onSubmit={handleOTPVerify} className="otp-form">
              <div className="otp-input-container">
                {[0, 1, 2, 3, 4, 5].map((index) => (
                  <input
                    key={index}
                    type="text"
                    className="otp-digit"
                    value={otp[index] || ''}
                    onChange={(e) => {
                      const value = e.target.value.replace(/\D/g, '').slice(0, 1);
                      const newOtp = otp.split('');
                      newOtp[index] = value;
                      setOtp(newOtp.join(''));
                      if (value && index < 5) {
                        document.querySelector(`.otp-digit:nth-child(${index + 2})`)?.focus();
                      }
                    }}
                    onKeyDown={(e) => {
                      if (e.key === 'Backspace' && !otp[index] && index > 0) {
                        document.querySelector(`.otp-digit:nth-child(${index})`)?.focus();
                      }
                    }}
                    maxLength={1}
                    disabled={loading}
                    autoFocus={index === 0}
                  />
                ))}
              </div>
              {error && <div className="error-message">{error}</div>}
              {success && <div className="success-message">{success}</div>}
              <button type="submit" className="btn-login" disabled={loading || otp.length !== 6}>
                {loading ? (
                  <span className="btn-loading">
                    <span className="spinner"></span>
                    Verifying...
                  </span>
                ) : (
                  'VERIFY OTP'
                )}
              </button>
              <button 
                type="button" 
                onClick={handleBackToLogin}
                className="btn-back"
                disabled={loading}
              >
                ‚Üê Back to Login
              </button>
            </form>
          </>
        )}
      </div>
      <div className="login-right">
         <img src="https://dce.harvard.edu/wp-content/uploads/sites/7/2023/12/choosing-classes.jpg" alt="Login illustration" />
      </div>
    </div>
  );
};

export default Login;
