import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import apiRequest from '../utils/api';

const Course = () => {
  const { id } = useParams();
  const [course, setCourse] = useState({});
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ rating: 1, comment: '' });

  useEffect(() => {
    loadCourseDetails();
    loadReviews();
  }, [id]);

  const loadCourseDetails = async () => {
    try {
      const data = await apiRequest(`/courses/${id}`, 'GET');
      setCourse(data);
    } catch (error) {
      console.error('Failed to load course details', error);
    }
  };

  const loadReviews = async () => {
    try {
      const data = await apiRequest(`/courses/${id}/reviews`, 'GET');
      setReviews(data);
    } catch (error) {
      console.error('Failed to load reviews', error);
    }
  };

  const handleEnroll = async () => {
    try {
      await apiRequest('/payments/enroll', 'POST', { courseId: id });
      alert('Enrolled successfully!');
    } catch (error) {
      console.error('Enrollment failed', error);
      alert('Enrollment failed. Please try again.');
    }
  };

  const handleDownloadCertificate = async () => {
    try {
      const response = await apiRequest(`/courses/${id}/certificate`, 'GET');
      // Handle certificate download
      window.open(response.url, '_blank');
    } catch (error) {
      console.error('Certificate download failed', error);
    }
  };

  const handleReviewSubmit = async (e) => {
    e.preventDefault();
    try {
      await apiRequest(`/courses/${id}/reviews`, 'POST', newReview);
      setNewReview({ rating: 1, comment: '' });
      loadReviews();
    } catch (error) {
      console.error('Review submission failed', error);
    }
  };

  return (
    <section className="course-details">
      <h2>{course.title}</h2>
      <p>{course.description}</p>
      <p><strong>Instructor:</strong> {course.instructor?.name}</p>
      <p><strong>Price:</strong> ${course.price}</p>
      <button onClick={handleEnroll}>Enroll</button>
      <button onClick={handleDownloadCertificate}>Download Certificate</button>

      {/* Reviews */}
      <section className="reviews">
        <h3>Reviews</h3>
        <div id="reviewsList">
          {reviews.map(review => (
            <div key={review._id}>
              <p>Rating: {review.rating}/5</p>
              <p>{review.comment}</p>
              <p>By: {review.user?.name}</p>
            </div>
          ))}
        </div>

        <h4>Add Your Review</h4>
        <form onSubmit={handleReviewSubmit}>
          <input
            type="number"
            value={newReview.rating}
            onChange={(e) => setNewReview(prev => ({ ...prev, rating: parseInt(e.target.value) }))}
            placeholder="Rating (1-5)"
            min="1"
            max="5"
            required
          />
          <textarea
            value={newReview.comment}
            onChange={(e) => setNewReview(prev => ({ ...prev, comment: e.target.value }))}
            placeholder="Comment"
            required
          />
          <button type="submit">Submit Review</button>
        </form>
      </section>
    </section>
  );
};

export default Course;
