import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import apiRequest from '../utils/api';
import './.css'; // use the same Login styles

const Register = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('student');
  const [bio, setBio] = useState('');
  const [skills, setSkills] = useState('');
  const [profilePicture, setProfilePicture] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('name', name);
      formData.append('email', email);
      formData.append('password', password);
      formData.append('role', role);
      formData.append('bio', bio);
      formData.append('skills', skills);
      if (profilePicture) {
        formData.append('profilePicture', profilePicture);
      }

      await apiRequest('/auth/register', 'POST', formData, false);
      alert('Registration successful! Please login.');
      navigate('/login');
    } catch (err) {
      alert(err.message || 'Registration failed. Please try again.');
      console.error(err);
    }
  };

  return (
    <div className="login-page" style={{ maxWidth: '1000px' }}>
      <div className="login-left">
        <h1>Create Account</h1>
        <p>Sign up for CertifyHub</p>

        <form onSubmit={handleSubmit} encType="multipart/form-data">
          <div className="input-group" style={{ maxWidth: '90%' }}>
            <span className="input-icon">üë§</span>
            <input
              type="text"
              placeholder="Full name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </div>

          <div className="input-group" style={{ maxWidth: '90%' }}>
            <span className="input-icon">üìß</span>
            <input
              type="email"
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="input-group" style={{ maxWidth: '90%' }}>
            <span className="input-icon">üîí</span>
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <div className="input-group" style={{ maxWidth: '90%' }}>
            <span className="input-icon">üìù</span>
            <textarea
              className="form-textarea"
              placeholder="Short bio"
              value={bio}
              onChange={(e) => setBio(e.target.value)}
              rows={3}
              style={{
                width: '100%',
                padding: '1rem 1rem 1rem 3rem',
                borderRadius: '20px',
                border: '1px solid #d3d8e0',
                resize: 'vertical',
                fontFamily: 'inherit',
              }}
            />
          </div>

          <div className="input-group" style={{ maxWidth: '90%' }}>
            <span className="input-icon">üí°</span>
            <input
              type="text"
              placeholder="Skills (comma separated)"
              value={skills}
              onChange={(e) => setSkills(e.target.value)}
            />
          </div>

          <div className="input-group" style={{ maxWidth: '90%' }}>
            <span className="input-icon">üßæ</span>
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              style={{
                width: '100%',
                padding: '1rem 1rem 1rem 3rem',
                borderRadius: '25px',
                border: '1px solid #d3d8e0',
                fontSize: '0.95rem',
                background: '#fff',
              }}
            >
              <option value="student">Student</option>
              <option value="instructor">Instructor</option>
            </select>
          </div>

          <div className="input-group" style={{ maxWidth: '90%' }}>
            <span className="input-icon">üñºÔ∏è</span>
            <label
              htmlFor="profilePicture"
              style={{
                display: 'block',
                width: '100%',
                padding: '1rem 1rem 1rem 3rem',
                borderRadius: '25px',
                border: '1px dashed #d3d8e0',
                cursor: 'pointer',
                color: '#666',
                background: '#fff',
                boxSizing: 'border-box',
              }}
            >
              {profilePicture ? profilePicture.name : 'Upload profile picture'}
              <input
                id="profilePicture"
                type="file"
                accept="image/*"
                onChange={(e) => setProfilePicture(e.target.files[0])}
                style={{ display: 'none' }}
              />
            </label>
          </div>

          <div className="form-footer" style={{ maxWidth: '90%' }}>
            <div style={{ fontSize: '0.9rem', color: '#606b85' }}>
              Already have an account? <Link to="/login" className="forgot-link">Login</Link>
            </div>
          </div>

          <button type="submit" className="btn-login" style={{ marginTop: '0.5rem' }}>
            Register
          </button>
        </form>

      </div>

      <div className="login-right">
        <img
          src="https://images.credly.com/images/139238b5-1896-46e8-a951-32dcad31fa21/image.png"
          alt="Register illustration"
        />
      </div>
    </div>
  );
};

export default Register;
