import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import apiRequest from '../utils/api';
import useAuth from '../hooks/useAuth';
import './Course.css';


const Course = () => {
  const { id } = useParams();
  const [course, setCourse] = useState(null);
  const [reviews, setReviews] = useState([]);
  const [rating, setRating] = useState(1);
  const [comment, setComment] = useState('');
  const { user } = useAuth();

  useEffect(() => {
    fetchCourse();
  }, [id]);

  const fetchCourse = async () => {
    try {
      const courseData = await apiRequest(`/courses/${id}`, 'GET');
      setCourse(courseData);
      setReviews(courseData.reviews || []);
    } catch (err) {
      console.error('Failed to load course', err);
    }
  };

  const handleEnroll = async () => {
    try {
      await apiRequest(`/courses/${id}/enroll`, 'POST', null, true);
      alert('Enrolled successfully!');
      fetchCourse();
    } catch (err) {
      let msg = 'Failed to enroll.';
      if (err && err.message) {
        msg += `\n${err.message}`;
      }
      alert(msg);
      console.error(err);
    }
  };

  const handleDownloadCert = () => {
    // Implement certificate download logic
    alert('Download certificate functionality to be implemented.');
  };

  const handleSubmitReview = async () => {
    try {
      await apiRequest(
        `/courses/${id}/reviews`,
        'POST',
        { rating, comment },
        true
      );
      alert('Review submitted!');
      setRating(1);
      setComment('');
      fetchCourse();
    } catch (err) {
      alert('Failed to submit review.');
      console.error(err);
    }
  };

  if (!course) {
    return <p>Loading course details...</p>;
  }

  return (
    <div>
      <section className="course-details" id="courseDetails">
        <h2 id="courseTitle">{course.title}</h2>
        <p id="courseDescription">{course.description}</p>
        <p><strong>Instructor:</strong> {course.instructor && course.instructor.name ? course.instructor.name : 'N/A'}</p>
        <p><strong>Price:</strong> ${course.price}</p>
        <button id="enrollBtn" onClick={handleEnroll}>Enroll</button>
        <button id="downloadCertBtn" onClick={handleDownloadCert}>Download Certificate</button>

        {/* Show enrolled students if user is instructor of this course */}
        {user && course.instructor && user._id === course.instructor._id && course.enrolledStudents && (
          <div style={{ marginTop: 24 }}>
            <h3>Enrolled Students</h3>
            {course.enrolledStudents.length === 0 ? (
              <p>No students enrolled yet.</p>
            ) : (
              <ul>
                {course.enrolledStudents.map((student) => (
                  <li key={student._id}>{student.name} ({student.email})</li>
                ))}
              </ul>
            )}
          </div>
        )}
      </section>

      <section className="reviews">
        <h3>Reviews</h3>
        <div id="reviewsList">
          {reviews.length === 0 ? (
            <p>No reviews yet.</p>
          ) : (
            reviews.map((review) => (
              <div key={review._id}>
                <p>Rating: {review.rating}</p>
                <p>{review.comment}</p>
              </div>
            ))
          )}
        </div>

        <h4>Add Your Review</h4>
        <input
          type="number"
          id="reviewRating"
          placeholder="Rating (1-5)"
          min="1"
          max="5"
          value={rating}
          onChange={(e) => setRating(Number(e.target.value))}
        />
        <textarea
          id="reviewComment"
          placeholder="Comment"
          value={comment}
          onChange={(e) => setComment(e.target.value)}
        />
        <button id="submitReviewBtn" onClick={handleSubmitReview}>Submit Review</button>
      </section>
    </div>
  );
};

export default Course;
