import React from 'react';
import { Link } from 'react-router-dom';
import './Footer.css';

const Footer = () => {
  const linkGroups = [
    {
      title: 'Learn',
      links: [
        { label: 'All courses', to: '/courses' },
        { label: 'Career tracks', to: '/courses' },
        { label: 'Certification prep', to: '/certificates' },
        { label: 'For teams', to: '/about' },
      ],
    },
    {
      title: 'Platform',
      links: [
        { label: 'Student dashboard', to: '/dashboard/student' },
        { label: 'Instructor tools', to: '/dashboard/instructor' },
        { label: 'Admin hub', to: '/dashboard/admin' },
        { label: 'Support', to: '/about' },
      ],
    },
    {
      title: 'Company',
      links: [
        { label: 'About', to: '/about' },
        { label: 'Careers', to: '/about' },
        { label: 'Newsroom', to: '/' },
        { label: 'Contact', to: '/about' },
      ],
    },
  ];

  return (
    <footer className="site-footer">
      <div className="footer-grid">
        <div className="footer-brand">
          <h3>CertifyHub</h3>
          <p>Industry-aligned credentials and pathways, crafted with universities and hiring partners.</p>
          <div className="footer-badges">
            <span className="badge badge-accent">ISO 27001 ready</span>
            <span className="badge">Trusted by 2K+ learners</span>
          </div>
        </div>

        {linkGroups.map((group) => (
          <div className="footer-links" key={group.title}>
            <h4>{group.title}</h4>
            <ul>
              {group.links.map((link) => (
                <li key={link.label}>
                  <Link to={link.to}>{link.label}</Link>
                </li>
              ))}
            </ul>
          </div>
        ))}

        <div className="footer-newsletter">
          <h4>Stay updated</h4>
          <p>Monthly insights, new launches, zero spam.</p>
          <form>
            <input type="email" placeholder="Email address" aria-label="Email address" />
            <button type="submit">Subscribe</button>
          </form>
        </div>
      </div>

      <div className="footer-bottom">
        <p>Â© {new Date().getFullYear()} CertifyHub. Built for lifelong learners.</p>
        <div className="footer-meta">
          <Link to="/">Privacy</Link>
          <Link to="/">Terms</Link>
          <Link to="/">Accessibility</Link>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
