import React, { useState, useEffect } from 'react';
import apiRequest from '../utils/api';
import './Instructor.css';

const Instructor = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');
  const [price, setPrice] = useState(0);
  const [myCourses, setMyCourses] = useState([]);

  useEffect(() => {
    fetchMyCourses();
  }, []);

  const fetchMyCourses = async () => {
    try {
      const courses = await apiRequest('/courses', 'GET', null, true);
      setMyCourses(courses);
    } catch (err) {
      console.error('❌ Error fetching courses:', err);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await apiRequest(
        '/courses',
        'POST',
        { title, description, category, price, modules: [] },
        true
      );
      alert('✅ Course created successfully!');
      fetchMyCourses();
      setTitle('');
      setDescription('');
      setCategory('');
      setPrice(0);
    } catch (err) {
      alert("❌ Failed to create course. Make sure you're logged in as instructor.");
      console.error(err);
    }
  };

  return (
    <section className="instructor-section">
      <h2>Instructor Dashboard</h2>

      {/* Create Course Form */}
      <div className="create-course">
        <h3>Create New Course</h3>
        <form onSubmit={handleSubmit}>
          <label htmlFor="title">Course Title:</label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />

          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            rows="4"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />

          <label htmlFor="category">Category:</label>
          <input
            type="text"
            id="category"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          />

          <label htmlFor="price">Price (0 for Free):</label>
          <input
            type="number"
            id="price"
            min="0"
            value={price}
            onChange={(e) => setPrice(Number(e.target.value))}
            required
          />

          <button type="submit">Create Course</button>
        </form>
      </div>

      {/* Existing Courses */}
      <div className="my-courses">
        <h3>My Courses</h3>
        <div id="myCoursesList">
          {myCourses.map((course) => (
            <div key={course._id} className="course-card">
              <h4>{course.title}</h4>
              <p>{course.description.substring(0, 100)}...</p>
              <p>Price: {course.price === 0 ? 'Free' : `$${course.price}`}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Instructor;
