import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import apiRequest from '../utils/api';
import './Login.css'; // Add this line to import the CSS file

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const data = await apiRequest('/auth/login', 'POST', { email, password });
      localStorage.setItem('token', data.token);
  window.location.href = '/';
    } catch (err) {
      alert('Login failed. Please check your credentials.');
      console.error(err);
    }
  };

  return (
    <div className="auth-page-container">
      <div className="auth-left-panel">
        <div className="auth-visual">
          <div className="auth-icon">ðŸŽ“</div> {/* Placeholder icon for completeness */}
          <h1 className="auth-title">Welcome to CertifyHub</h1>
          <p className="auth-description">Your gateway to skill-building and certification. Join thousands of learners worldwide.</p>
        </div>
      </div>
      <div className="auth-right-panel">
        <div className="auth-container glass-container">
          <div className="auth-header">
            <h2>Welcome Back</h2>
            <p>Sign in to your CertifyHub account</p>
          </div>
          <form id="login-form" className="auth-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Email Address</label>
              <input
                type="email"
                id="email"
                className="form-input"
                placeholder="Enter your email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label className="form-label">Password</label>
              <input
                type="password"
                id="password"
                className="form-input"
                placeholder="Enter your password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <button type="submit" className="btn btn-primary btn-full">Login</button>
          </form>
          <div className="auth-footer">
            <p>Don't have an account? <Link to="/register" className="auth-link">Create Account</Link></p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
