import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import apiRequest from '../utils/api';

const CreateCourse = () => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');
  const [price, setPrice] = useState(0);
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await apiRequest(
        '/courses',
        'POST',
        { title, description, category, price, modules: [] },
        true
      );
      alert('✅ Course created successfully!');
      navigate('/instructor');
    } catch (err) {
      alert("❌ Failed to create course. Make sure you're logged in as instructor.");
      console.error(err);
    }
  };

  return (
    <div>
      <header>
        <button onClick={() => navigate('/')}>⬅ Back Home</button>
        <button onClick={() => navigate('/courses')}>Courses</button>
      </header>

      <main>
        <h1>Create a New Course</h1>
        <form id="create-course-form" onSubmit={handleSubmit}>
          <label htmlFor="title">Course Title:</label>
          <input
            type="text"
            id="title"
            required
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          /><br /><br />

          <label htmlFor="description">Description:</label>
          <textarea
            id="description"
            required
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          /><br /><br />

          <label htmlFor="category">Category:</label>
          <input
            type="text"
            id="category"
            required
            value={category}
            onChange={(e) => setCategory(e.target.value)}
          /><br /><br />

          <label htmlFor="price">Price (leave 0 for free):</label>
          <input
            type="number"
            id="price"
            min="0"
            value={price}
            onChange={(e) => setPrice(Number(e.target.value))}
          /><br /><br />

          <button type="submit">Create Course</button>
        </form>
      </main>
    </div>
  );
};

export default CreateCourse;
