import React, { useState } from 'react';
import apiRequest from '../utils/api';
import './Certificate.css';

const Certificate = ({ certificate, onDownload }) => {
  const [loading, setLoading] = useState(false);

  const handleDownload = async () => {
    if (onDownload) {
      onDownload(certificate);
      return;
    }

    setLoading(true);
    try {
      const response = await fetch(
        `http://localhost:5000/api/certificates/courses/${certificate.course._id}/certificate?userId=${certificate.student._id}`,
        {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          }
        }
      );

      if (!response.ok) {
        throw new Error('Failed to download certificate');
      }

      // Get the certificate URL from the response
      const data = await response.json();

      // Create download link
      const link = document.createElement('a');
      link.href = `http://localhost:5000${data.certificateUrl}`;
      link.download = `certificate-${certificate.course.title.replace(/\s+/g, '-')}.pdf`;
      link.target = '_blank';
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);

    } catch (error) {
      console.error('Download failed:', error);
      alert('Failed to download certificate: ' + error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleView = () => {
    if (certificate.certificateUrl) {
      window.open(`http://localhost:5000${certificate.certificateUrl}`, '_blank');
    }
  };

  return (
    <div className="certificate-card glass-card">
      <div className="certificate-header">
        <div className="certificate-icon">
          <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M12 15L8 11L9.414 9.586L12 12.172L14.586 9.586L16 11L12 15Z" fill="currentColor"/>
            <path d="M4 3H20C20.5523 3 21 3.44772 21 4V16C21 16.5523 20.5523 17 20 17H4C3.44772 17 3 16.5523 3 16V4C3 3.44772 3.44772 3 4 3Z" stroke="currentColor" strokeWidth="2"/>
            <path d="M8 21L12 19L16 21V17H8V21Z" fill="currentColor"/>
          </svg>
        </div>
        <div className="certificate-info">
          <h3>{certificate.course?.title || 'Course Certificate'}</h3>
          <p className="student-name">{certificate.student?.name || 'Student'}</p>
          <div className="certificate-details">
            {certificate.grade && (
              <span className="grade-badge">{certificate.grade}</span>
            )}
            {certificate.overallScore && (
              <span className="score-badge">{certificate.overallScore}%</span>
            )}
          </div>
        </div>
      </div>

      <div className="certificate-meta">
        <div className="meta-item">
          <span className="meta-label">Issued Date:</span>
          <span className="meta-value">
            {new Date(certificate.issuedAt).toLocaleDateString()}
          </span>
        </div>
        {certificate.certificateId && (
          <div className="meta-item">
            <span className="meta-label">Certificate ID:</span>
            <span className="meta-value">{certificate.certificateId}</span>
          </div>
        )}
      </div>

      <div className="certificate-actions">
        <button 
          className="btn btn-secondary"
          onClick={handleView}
          disabled={!certificate.certificateUrl}
        >
          <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M15 12C15 13.6569 13.6569 15 12 15C10.3431 15 9 13.6569 9 12C9 10.3431 10.3431 9 12 9C13.6569 9 15 10.3431 15 12Z" stroke="currentColor" strokeWidth="2"/>
            <path d="M12.0003 5C7.52443 5 3.73132 7.94288 2.45825 12C3.73132 16.0571 7.52443 19 12.0003 19C16.4761 19 20.2692 16.0571 21.5423 12C20.2692 7.94288 16.4761 5 12.0003 5Z" stroke="currentColor" strokeWidth="2"/>
          </svg>
          View
        </button>
        <button 
          className="btn btn-primary"
          onClick={handleDownload}
          disabled={loading || !certificate.certificateUrl}
        >
          <svg viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
            <path d="M12 16L7 11L8.4 9.6L11 12.2V4H13V12.2L15.6 9.6L17 11L12 16Z" fill="currentColor"/>
            <path d="M5 20V18H19V20H5Z" fill="currentColor"/>
          </svg>
          {loading ? 'Downloading...' : 'Download'}
        </button>
      </div>
    </div>
  );
};

export default Certificate;
